#!/usr/bin/env node
/* eslint-disable no-console */
const connectDB = require('../config/db');

async function main() {
  const start = Date.now();
  await connectDB();

  const StockBalance = require('../models/stock/StockBalance'); // StockBalanceArch
  const StockOperation = require('../models/stock/StockOperation');

  try {
    await StockBalance.syncIndexes();
    await StockOperation.syncIndexes();
    const duration = Date.now() - start;
    console.log('[stock.indexes] OK: indexes synced for StockBalanceArch + StockOperation');
    console.log(`[stock.indexes] duration=${duration}ms`);
    process.exit(0);
  } catch (err) {
    console.error('[stock.indexes] ERROR:', err && err.message);
    process.exit(1);
  }
}

main();

// Middleware guard for stock-related routes based on ENABLE_STOCKS flag
module.exports.requireStocksEnabled = function requireStocksEnabled(req, res, next) {
  const v = String(process.env.ENABLE_STOCKS || '1').toLowerCase();
  const enabled = v === '1' || v === 'true';
  if (!enabled) return res.status(404).json({ ok: false, error: 'STOCKS_DISABLED' });
  return next();
};

const mongoose = require('mongoose');

const { Schema } = mongoose;

// New stock balance architecture (Week 1): quantity + reservedQuantity
// Note: Uses a distinct model name to avoid collision with existing server/models/StockBalance
const StockBalanceSchema = new Schema({
  itemId: { type: Schema.Types.ObjectId, ref: 'Item', required: true },
  locationId: { type: Schema.Types.ObjectId, ref: 'Location', required: true },
  quantity: { type: Number, default: 0 },
  reservedQuantity: { type: Number, default: 0 },
  lastUpdatedAt: { type: Date, default: () => new Date() },
}, { timestamps: true, versionKey: false });

// Unique composite index per item/location
StockBalanceSchema.index({ itemId: 1, locationId: 1 }, { unique: true, name: 'uniq_item_location' });

// Optional sanity: ensure quantities are not negative (soft validator)
StockBalanceSchema.path('quantity').validate((v) => v === undefined || v >= 0, 'QUANTITY_NEGATIVE');
StockBalanceSchema.path('reservedQuantity').validate((v) => v === undefined || v >= 0, 'RESERVED_NEGATIVE');

module.exports = mongoose.models.StockBalanceArch || mongoose.model('StockBalanceArch', StockBalanceSchema);

const mongoose = require('mongoose');

const { Schema } = mongoose;

// StockOperation: canonical operation log for stock movements
const StockOperationSchema = new Schema({
  type: { type: String, enum: ['in', 'out', 'return', 'transfer'], required: true },
  itemId: { type: Schema.Types.ObjectId, ref: 'Item', required: true },
  qty: { type: Number, required: true },
  locationIdFrom: { type: Schema.Types.ObjectId, ref: 'Location' },
  locationIdTo: { type: Schema.Types.ObjectId, ref: 'Location' },
  sourceType: { type: String },
  sourceId: { type: Schema.Types.ObjectId },
  performedBy: { type: Schema.Types.ObjectId, ref: 'User' },
  createdAt: { type: Date, default: () => new Date() },
}, { timestamps: true, versionKey: false });

// Indexes per spec
StockOperationSchema.index({ itemId: 1, createdAt: -1 }, { name: 'op_item_createdAt' });
StockOperationSchema.index({ sourceType: 1, sourceId: 1 }, { name: 'op_source' });

module.exports = mongoose.models.StockOperation || mongoose.model('StockOperation', StockOperationSchema);

const express = require('express');
const router = express.Router();
const { requireAuth, requirePermission } = require('../middleware/auth');
const { requireStocksEnabled } = require('../middleware/featureFlags/stock');
const { validate } = require('../middleware/validate');
const { adjustSchema, transferSchema } = require('../validation/stock');
const stockService = require('../services/stock/stockService');

router.use(requireStocksEnabled);
router.use(requireAuth);

// GET /api/stocks — list balances by filters
router.get('/', requirePermission('stocks.read'), async (req, res) => {
  try {
    const { itemId, locationId } = req.query;
    const limit = Math.min(parseInt(req.query.limit, 10) || 50, 200);
    const offset = parseInt(req.query.offset, 10) || 0;
    const result = await stockService.listBalances({ itemId, locationId, limit, offset });
    res.json(result);
  } catch (err) {
    res.status(err.statusCode || 500).json({ ok: false, error: err.message });
  }
});

// POST /api/stocks/adjust — manual adjustment (+/-)
router.post('/adjust', requirePermission('stocks.adjust'), validate(adjustSchema), async (req, res) => {
  try {
    const { itemId, locationId, qty, note } = req.body;
    const result = await stockService.adjust({ itemId, locationId, qty, note, userId: req.user && req.user._id });
    res.json(result);
  } catch (err) {
    const code = err.statusCode === 409 ? 409 : (err.statusCode || 500);
    res.status(code).json({ ok: false, error: err.message });
  }
});

// POST /api/stocks/transfer — move stock between locations
router.post('/transfer', requirePermission('stocks.transfer'), validate(transferSchema), async (req, res) => {
  try {
    const { itemId, from, to, qty, note } = req.body;
    const result = await stockService.transfer({ itemId, from, to, qty, note, userId: req.user && req.user._id });
    res.json(result);
  } catch (err) {
    const code = err.statusCode === 409 ? 409 : (err.statusCode || 500);
    res.status(code).json({ ok: false, error: err.message });
  }
});

module.exports = router;

#!/usr/bin/env node
/* eslint-disable no-console */
const fs = require('fs');
const path = require('path');
const csvParse = require('csv-parse/sync');
const mongoose = require('mongoose');
const connectDB = require('../../config/db');

async function loadCsvIfExists() {
  const candidates = [
    path.join(process.cwd(), 'storage', 'import', 'Склад.csv'),
    path.join(process.cwd(), 'storage', 'import', 'stock.csv'),
  ];
  for (const p of candidates) {
    if (fs.existsSync(p)) {
      const buf = fs.readFileSync(p);
      const rows = csvParse.parse(buf, {
        columns: true,
        skip_empty_lines: true,
      });
      return rows; // Expect columns: itemCode|itemId, locationId, quantity, reservedQuantity
    }
  }
  return null;
}

async function main() {
  const start = Date.now();
  await connectDB();

  const StockBalance = require('../../models/stock/StockBalance'); // StockBalanceArch
  const StockOperation = require('../../models/stock/StockOperation');
  let processed = 0; let totalQty = 0;

  try {
    const csvRows = await loadCsvIfExists();
    if (csvRows && csvRows.length) {
      for (const r of csvRows) {
        const itemId = r.itemId ? mongoose.Types.ObjectId(r.itemId) : undefined;
        const locationId = r.locationId ? mongoose.Types.ObjectId(r.locationId) : undefined;
        if (!itemId || !locationId) continue;
        const quantity = Number(r.quantity || 0);
        const reservedQuantity = Number(r.reservedQuantity || 0);
        await StockBalance.updateOne(
          { itemId, locationId },
          { $set: { quantity, reservedQuantity, lastUpdatedAt: new Date(), } },
          { upsert: true },
        );
        processed += 1; totalQty += quantity;
      }
    } else {
      // Fallback: derive from existing StockItem and StockMovement if present
      let StockItem; let StockMovement;
      try { StockItem = require('../../server/models/StockItem'); } catch (e) {}
      try { StockMovement = require('../../server/models/StockMovement'); } catch (e) {}

      if (StockItem) {
        const items = await StockItem.find({}).lean();
        for (const it of items) {
          const itemId = it.itemId || it._id; // prefer itemId if denormalized
          const locationId = it.locationId || undefined;
          const quantity = Number(it.qtyOnHand || 0);
          const reservedQuantity = 0;
          if (!itemId) continue;
          if (!locationId) continue; // enforce location pairing for unique index
          await StockBalance.updateOne(
            { itemId, locationId },
            { $set: { quantity, reservedQuantity, lastUpdatedAt: new Date(), } },
            { upsert: true },
          );
          processed += 1; totalQty += quantity;
        }
      }

    }

    const duration = Date.now() - start;
    console.log(`[stocks.backfill] processed=${processed} totalQty=${totalQty} duration=${duration}ms`);
    process.exit(0);
  } catch (err) {
    console.error('[stocks.backfill] ERROR:', err && err.message);
    process.exit(1);
  }
}

main();

const express = require('express');
const cors = require('cors');
const helmet = require('helmet');
const path = require('path');
const connectDB = require('./config/db');
require('dotenv').config();

// Config validator
const { validateEnv, logEnvValidation } = require('./services/configValidator');

const envValidation = validateEnv(process.env);
logEnvValidation(envValidation);

const DEV_MODE = process.env.AUTH_DEV_MODE === '1';
// Connect to Database (skip in dev auth mode)
if (!DEV_MODE) {
  connectDB();
} else {
  console.log('Auth DEV mode enabled: skipping MongoDB connection');
}

// Initialize status action queue (Queue + Worker + QueueScheduler)
require('./queues/statusActionQueue');

try { require('./server/models/Payment'); require('./server/models/CashRegister'); } catch (e) {}

const app = express();

// Middleware
app.use(express.json());
app.use(helmet());
// CORS: strict in production, permissive in dev
const allowedOrigins = (process.env.CORS_ORIGIN || '').split(',').map((s) => s.trim()).filter(Boolean);
const corsOptions = process.env.NODE_ENV === 'production'
  ? {
    origin: (origin, cb) => {
      if (!origin || allowedOrigins.includes(origin)) return cb(null, true);
      return cb(new Error('Not allowed by CORS'));
    },
    credentials: true,
    methods: ['GET', 'POST', 'PATCH', 'PUT', 'DELETE', 'OPTIONS', 'HEAD'],
  }
  : { origin: true, credentials: true };
app.use(cors(corsOptions));
app.use(require('./middleware/auth').withUser);
// Block dangerous HTTP methods in production
if (process.env.NODE_ENV === 'production') {
  const blocked = new Set(['TRACE', 'TRACK']);
  app.use((req, res, next) => {
    if (blocked.has(req.method)) return res.status(405).send('Method Not Allowed');
    return next();
  });
}
// Add global API auth guard (whitelist /api/auth/* and /api/public/*)
const { requireAuth } = require('./middleware/auth');

app.use('/api', (req, res, next) => {
  const p = req.path || '';
  if (p.startsWith('/auth') || p.startsWith('/public')) return next();
  return requireAuth(req, res, next);
});

// Error handler middleware (должен быть после всех маршрутов)
const errorHandler = require('./middleware/error');

// Basic API route for testing
app.get('/api/test', (req, res) => {
  res.json({ msg: 'API is working' });
});

// Define API routes
app.use('/api/clients', require('./routes/clients'));
app.use('/api/detailing-orders', require('./routes/detailingOrders'));
app.use('/api/boxes', require('./routes/boxes'));
app.use('/api/tasks', require('./routes/tasks'));
// New routes: users, roles, auth
app.use('/api/users', require('./routes/users'));
app.use('/api/roles', require('./routes/roles'));
app.use('/api/statuses', require('./routes/statuses'));
app.use('/api/orders', require('./routes/orders'));
app.use('/api/payments', require('./routes/payments'));
app.use('/api/cash', require('./routes/cash'));
app.use('/api/items', require('./routes/items'));
app.use('/api/notify/templates', require('./routes/notifyTemplates'));
app.use('/api/notify/dev', require('./routes/notifyDev'));
app.use('/api/doc-templates', require('./routes/docTemplates'));
app.use('/api/order-types', require('./routes/orderTypes'));
app.use('/api/files', require('./routes/files'));
app.use('/api/auth', require('./routes/auth'));
app.use('/api/public', require('./routes/public'));
app.use('/api/queue', require('./routes/queue'));
app.use('/api/reports', require('./routes/reports'));
app.use('/api/reports/stocks', require('./routes/reports/stocks'));

// 3.3 Step 2: FieldSchemas & Dictionaries
app.use('/api/fields', require('./routes/fields'));
app.use('/api/dicts', require('./routes/dicts'));

// Employees & Payroll
app.use('/api/employees', require('./routes/employees'));
app.use('/api/payroll', require('./routes/payrollRules'));
app.use('/api/payroll', require('./routes/payrollAccruals'));

// Warehouse stock routes
app.use('/api/stock', require('./routes/stock'));
app.use('/api/stocks', require('./routes/stocks'));
app.use('/api/doc-templates', require('./routes/docTemplates'));
// Shop
app.use('/api/shop', require('./routes/shop'));

// Error handler
app.use(errorHandler);

// Serve static assets in production
if (process.env.NODE_ENV === 'production') {
  app.use(express.static('client/build'));

  app.get('*', (req, res) => {
    res.sendFile(path.resolve(__dirname, 'client', 'build', 'index.html'));
  });
}

const PORT = process.env.PORT || 5002;

app.listen(PORT, () => console.log(`Server started on port ${PORT}`));
const minLevelWatcher = require('./services/stock/minLevelWatcher');
minLevelWatcher.start();

const mongoose = require('mongoose');

let StockBalance; let StockOperation;
try { StockBalance = require('../../models/stock/StockBalance'); } catch (e) {}
try { StockOperation = require('../../models/stock/StockOperation'); } catch (e) {}

const mongoReady = () => mongoose.connection && mongoose.connection.readyState === 1;
const httpError = (statusCode, message) => { const err = new Error(message); err.statusCode = statusCode; return err; };

function parseDate(val) {
  const d = val ? new Date(String(val)) : null;
  return d && !isNaN(d) ? d : null;
}

// GET stocks summary by location
async function summaryByLocation({ limit = 10 } = {}) {
  if (!StockBalance) throw httpError(500, 'MODEL_NOT_AVAILABLE');
  if (!mongoReady()) return { ok: true, groups: [], totalQty: 0 };

  const agg = await StockBalance.aggregate([
    { $group: { _id: '$locationId', sumQty: { $sum: '$quantity' }, sumReserved: { $sum: '$reservedQuantity' } } },
    { $sort: { sumQty: -1 } },
    { $limit: Math.max(1, Math.min(200, Number(limit) || 10)) },
  ]);

  const groups = agg.map((row) => ({
    locationId: row._id ? String(row._id) : undefined,
    totals: { qty: Number(row.sumQty || 0), reserved: Number(row.sumReserved || 0), available: Number(row.sumQty || 0) - Number(row.sumReserved || 0) },
  }));
  const totalQty = groups.reduce((acc, g) => acc + g.totals.qty, 0);
  return { ok: true, groups, totalQty };
}

// GET stocks turnover for period
async function turnover({ from, to, limit = 10 } = {}) {
  if (!StockOperation) throw httpError(500, 'MODEL_NOT_AVAILABLE');
  if (!mongoReady()) return { ok: true, totals: { in: 0, out: 0, net: 0 }, byItem: [] };

  const match = {};
  const df = parseDate(from);
  const dt = parseDate(to);
  if (df || dt) {
    match.createdAt = {};
    if (df) match.createdAt.$gte = df;
    if (dt) match.createdAt.$lte = dt;
  }

  const totalsAgg = await StockOperation.aggregate([
    { $match: match },
    { $group: { _id: '$type', sum: { $sum: '$qty' } } },
  ]);
  let totalIn = 0; let totalOut = 0;
  totalsAgg.forEach((row) => {
    const t = String(row._id || '');
    const s = Number(row.sum || 0);
    if (t === 'in' || t === 'return') totalIn += s;
    else if (t === 'out') totalOut += s;
  });

  const byItemAgg = await StockOperation.aggregate([
    { $match: match },
    { $group: { _id: { itemId: '$itemId', type: '$type' }, sum: { $sum: '$qty' } } },
  ]);
  const map = new Map();
  byItemAgg.forEach((row) => {
    const id = String(row._id.itemId || '');
    const t = String(row._id.type || '');
    const s = Number(row.sum || 0);
    const cur = map.get(id) || { itemId: id, in: 0, out: 0 };
    if (t === 'in' || t === 'return') cur.in += s; else if (t === 'out') cur.out += s;
    map.set(id, cur);
  });
  const byItem = Array.from(map.values())
    .map((g) => ({ itemId: g.itemId, in: g.in, out: g.out, net: g.in - g.out }))
    .sort((a, b) => (b.in + b.out) - (a.in + a.out))
    .slice(0, Math.max(1, Math.min(200, Number(limit) || 10)));

  return { ok: true, totals: { in: totalIn, out: totalOut, net: totalIn - totalOut }, byItem };
}

module.exports = { summaryByLocation, turnover };

const mongoose = require('mongoose');

let StockItem; let StockBalance;
try { StockItem = require('../../server/models/StockItem'); } catch (e) {}
try { StockBalance = require('../../models/stock/StockBalance'); } catch (e) {}

const TemplatesStore = require('../templatesStore');

let telegramNotify; try { telegramNotify = require('../telegramNotify'); } catch (e) {}

const mongoReady = () => mongoose.connection && mongoose.connection.readyState === 1;
let _timer = null;

async function scanAndNotify() {
  if (!mongoReady()) { console.warn('[minStock] skip: DB_NOT_READY'); return { ok: true, skipped: true }; }
  if (!StockItem) { console.warn('[minStock] skip: MODEL_NOT_AVAILABLE'); return { ok: true, skipped: true }; }

  const items = await StockItem.find({ minQty: { $gt: 0 } }).lean();
  const alerts = [];
  for (const it of (items || [])) {
    const { itemId, locationId: locId, minQty } = it;
    const min = Number(minQty || 0);
    if (!itemId || !locId || !(min > 0)) continue;
    let qty = Number(it.qtyOnHand || 0);
    try {
      if (StockBalance) {
        const bal = await StockBalance.findOne({ itemId, locationId: locId }).lean();
        if (bal) { qty = Number(bal.quantity || 0) - Number(bal.reservedQuantity || 0); }
      }
    } catch (e) {}
    if (qty <= min) {
      alerts.push({ itemId: String(itemId), locationId: String(locId), qty, minQty: min });
    }
  }

  if (alerts.length === 0) return { ok: true, alerts: [] };

  const tpl = TemplatesStore.getNotifyTemplate('min-stock') || { subject: 'Минимальный остаток', bodyHtml: '<p>Товар {{itemId}} на локации {{locationId}} достиг минимума ({{qty}} ≤ {{minQty}})</p>' };

  for (const a of alerts) {
    // Log
    console.warn('[minStock] ALERT', a);

    // Email via SMTP (simple fallback based on statusActionsHandler notifyAdapter pattern)
    try {
      const DRY = process.env.NOTIFY_DRY_RUN === '1';
      const host = process.env.SMTP_HOST; const port = Number(process.env.SMTP_PORT || 0) || 587;
      const user = process.env.SMTP_USER; const pass = process.env.SMTP_PASS;
      const from = process.env.SMTP_FROM || 'no-reply@example.com';
      const to = process.env.SMTP_TO || 'dev@example.com';
      const subject = String(tpl.subject || 'Минимальный остаток');
      const html = String((tpl.bodyHtml || '').replace(/\{\{\s*itemId\s*\}\}/g, a.itemId).replace(/\{\{\s*locationId\s*\}\}/g, a.locationId).replace(/\{\{\s*qty\s*\}\}/g, String(a.qty)).replace(/\{\{\s*minQty\s*\}\}/g, String(a.minQty)));
      if (!DRY && host && user && pass) {
        const nodemailer = require('nodemailer');
        const transporter = nodemailer.createTransport({ host, port, secure: port === 465, auth: { user, pass } });
        await transporter.sendMail({ from, to, subject, html });
        console.log('[minStock] email sent', { to, subject });
      } else {
        console.log('[minStock] email DRY or SMTP missing, subject:', subject);
      }
    } catch (e) {
      console.warn('[minStock] email send error', e && e.message);
    }

    // Telegram
    try {
      if (telegramNotify && typeof telegramNotify.sendMessage === 'function') {
        await telegramNotify.sendMessage(`⚠️ Мин. остаток: item=${a.itemId} loc=${a.locationId} qty=${a.qty} min=${a.minQty}`);
      }
    } catch (e) {
      console.warn('[minStock] telegram send error', e && e.message);
    }

    // Webhook/callback
    try {
      const url = process.env.MIN_STOCK_WEBHOOK_URL;
      if (url) {
        let axios; try { axios = require('axios'); } catch (e) {}
        if (axios) {
          await axios.post(url, { event: 'min_stock', payload: a });
          console.log('[minStock] webhook sent', { url });
        } else {
          console.warn('[minStock] axios not installed — TODO integrate HTTP client');
        }
      }
    } catch (e) {
      console.warn('[minStock] webhook error', e && e.message);
    }
  }

  return { ok: true, alerts };
}

function start() {
  if (_timer) return { ok: true, already: true };
  const enabled = String(process.env.MIN_STOCK_WATCHER_ENABLED || '1') === '1';
  if (!enabled) return { ok: true, skipped: true };
  const intervalMs = Number(process.env.MIN_STOCK_CHECK_INTERVAL_MS || 60000);
  _timer = setInterval(() => { scanAndNotify().catch((e) => console.warn('[minStock] scan error', e && e.message)); }, intervalMs);
  console.log('[minStock] watcher started', { intervalMs });
  return { ok: true };
}

function stop() { if (_timer) { clearInterval(_timer); _timer = null; } return { ok: true }; }

module.exports = { start, stop, scanAndNotify };

const mongoose = require('mongoose');

let StockBalance; let Order;
try { StockBalance = require('../../models/stock/StockBalance'); } catch (e) {}
try { Order = require('../../models/Order'); } catch (e) {}

const mongoReady = () => mongoose.connection && mongoose.connection.readyState === 1;
const httpError = (statusCode, message) => { const err = new Error(message); err.statusCode = statusCode; return err; };
function asObjId(id) { try { return new mongoose.Types.ObjectId(String(id)); } catch (e) { return undefined; } }

function normalizeItems(items) {
  const map = new Map();
  const arr = Array.isArray(items) ? items : [];
  for (const it of arr) {
    const id = it && it.itemId;
    const qty = Number((it && it.qty) || 0);
    if (!id || qty <= 0) continue;
    const key = String(id);
    const prev = map.get(key) || 0;
    map.set(key, prev + qty);
  }
  return map; // key:string itemId -> total qty
}

// Reserve: increase reservedQuantity for order items; forbid negative available
async function reserveForOrder({ orderId, locationId, userId }) {
  if (!StockBalance || !Order) throw httpError(500, 'MODEL_NOT_AVAILABLE');
  if (!mongoReady()) throw httpError(503, 'DB_NOT_READY');
  const order = await Order.findById(orderId).lean();
  if (!order || !Array.isArray(order.items) || order.items.length === 0) return { ok: true, reserved: 0 };
  const loc = locationId ? asObjId(locationId) : (process.env.DEFAULT_STOCK_LOCATION_ID ? asObjId(process.env.DEFAULT_STOCK_LOCATION_ID) : undefined);
  if (!loc) return { ok: true, skipped: true };

  const itemsMap = normalizeItems(order.items);
  const session = await mongoose.startSession();
  session.startTransaction();

  try {
    let count = 0;
    for (const [itemIdStr, qty] of itemsMap.entries()) {
      const itemObj = asObjId(itemIdStr);
      const bal = await StockBalance.findOne({ itemId: itemObj, locationId: loc }).session(session);
      const quantity = bal ? Number(bal.quantity || 0) : 0;
      const reserved = bal ? Number(bal.reservedQuantity || 0) : 0;
      const available = quantity - reserved;
      if (available < qty) throw httpError(409, 'INSUFFICIENT_STOCK');
      await StockBalance.updateOne(
        { itemId: itemObj, locationId: loc },
        { $set: { lastUpdatedAt: new Date() }, $inc: { reservedQuantity: Math.abs(qty) } },
        { upsert: true, session },
      );
      count += 1;
    }
    await session.commitTransaction();
    session.endSession();
    return { ok: true, reserved: count };
  } catch (err) {
    try { await session.abortTransaction(); } catch {}
    session.endSession();
    throw err;
  }
}

// Release: decrease reservedQuantity for order items (clamp to >=0)
async function releaseForOrder({ orderId, locationId, userId }) {
  if (!StockBalance || !Order) throw httpError(500, 'MODEL_NOT_AVAILABLE');
  if (!mongoReady()) throw httpError(503, 'DB_NOT_READY');
  const order = await Order.findById(orderId).lean();
  if (!order || !Array.isArray(order.items) || order.items.length === 0) return { ok: true, released: 0 };
  const loc = locationId ? asObjId(locationId) : (process.env.DEFAULT_STOCK_LOCATION_ID ? asObjId(process.env.DEFAULT_STOCK_LOCATION_ID) : undefined);
  if (!loc) return { ok: true, skipped: true };

  const itemsMap = normalizeItems(order.items);
  const session = await mongoose.startSession();
  session.startTransaction();

  try {
    let count = 0;
    for (const [itemIdStr, qty] of itemsMap.entries()) {
      const itemObj = asObjId(itemIdStr);
      const bal = await StockBalance.findOne({ itemId: itemObj, locationId: loc }).session(session);
      const reserved = bal ? Number(bal.reservedQuantity || 0) : 0;
      const nextReserved = Math.max(0, reserved - Math.abs(qty));
      await StockBalance.updateOne(
        { itemId: itemObj, locationId: loc },
        { $set: { lastUpdatedAt: new Date(), reservedQuantity: nextReserved } },
        { upsert: true, session },
      );
      count += 1;
    }
    await session.commitTransaction();
    session.endSession();
    return { ok: true, released: count };
  } catch (err) {
    try { await session.abortTransaction(); } catch {}
    session.endSession();
    throw err;
  }
}

// Edit diff: apply reservation changes based on items delta
async function applyDiffForOrderEdit({ orderId, prevItems, nextItems, locationId, userId }) {
  if (!StockBalance) throw httpError(500, 'MODEL_NOT_AVAILABLE');
  if (!mongoReady()) throw httpError(503, 'DB_NOT_READY');
  const loc = locationId ? asObjId(locationId) : (process.env.DEFAULT_STOCK_LOCATION_ID ? asObjId(process.env.DEFAULT_STOCK_LOCATION_ID) : undefined);
  if (!loc) return { ok: true, skipped: true };

  const prevMap = normalizeItems(prevItems);
  const nextMap = normalizeItems(nextItems);
  const allKeys = new Set([...prevMap.keys(), ...nextMap.keys()]);
  const session = await mongoose.startSession();
  session.startTransaction();

  try {
    let adjusted = 0;
    for (const key of allKeys) {
      const prevQty = prevMap.get(key) || 0;
      const nextQty = nextMap.get(key) || 0;
      const delta = nextQty - prevQty;
      if (delta === 0) continue;
      const itemObj = asObjId(key);
      const bal = await StockBalance.findOne({ itemId: itemObj, locationId: loc }).session(session);
      const quantity = bal ? Number(bal.quantity || 0) : 0;
      const reserved = bal ? Number(bal.reservedQuantity || 0) : 0;
      if (delta > 0) {
        const available = quantity - reserved;
        if (available < delta) throw httpError(409, 'INSUFFICIENT_STOCK');
        await StockBalance.updateOne(
          { itemId: itemObj, locationId: loc },
          { $set: { lastUpdatedAt: new Date() }, $inc: { reservedQuantity: Math.abs(delta) } },
          { upsert: true, session },
        );
      } else {
        const nextReserved = Math.max(0, reserved - Math.abs(delta));
        await StockBalance.updateOne(
          { itemId: itemObj, locationId: loc },
          { $set: { lastUpdatedAt: new Date(), reservedQuantity: nextReserved } },
          { upsert: true, session },
        );
      }
      adjusted += 1;
    }

    await session.commitTransaction();
    session.endSession();
    return { ok: true, adjusted };
  } catch (err) {
    try { await session.abortTransaction(); } catch {}
    session.endSession();
    throw err;
  }
}

